$(function() {
    $("*[include-html]").each(function() {
        var markdownFile = $(this).attr("include-html"),
            targetElementId = this.id;
        
        // Initialize the Showdown converter
        var converter = new showdown.Converter();

        jQuery.ajax({
            url: markdownFile,
            // Fetch the Markdown file as plain text
            dataType: "text", 
            success: function(markdownText) {
                // Convert the Markdown text to HTML
                var htmlContent = converter.makeHtml(markdownText);
                // Insert the converted HTML into the target element
                $("#" + targetElementId).html(htmlContent);
            },
            error: function(jqXHR, textStatus, errorThrown) {
                var errorMessage = jqXHR.status + ": " + jqXHR.statusText;
                $("#" + targetElementId).html(markdownFile + " - " + errorMessage);
            }
        });
    });
});
